import React, {useEffect} from "react";
import "./index.css";
import AppContext from "../../context";
import { BrowserRouter, Route, Switch } from "react-router-dom";
import TwittersView from "../TwittersView/TwittersView";
import ArticlesView from "../ArticlesView/ArticlesView";
import NotesView from "../NotesView/NotesView";
import Header from "../../components/Header/Header";
import Modal from "../../components/Modal/Modal";

import DecorationArrow from "../../components/DecorationComponents/DecorationArrow/DecorationArrow"
import ImagePort from "../../components/ImagePort/ImagePort"
import Logo from "../../components/Logo/Logo"
import img1A from "../../Photos/A1.jpg"
import img1B from "../../Photos/A2.jpg"
import img2A from "../../Photos/B1.jpg"
import img2B from "../../Photos/B2.jpg"
import img3A from "../../Photos/C1.jpg"
import img3B from "../../Photos/C2.jpg"
import downArrowGraphic from "../../down-arrow.png"

import Aos from 'aos'
import "aos/dist/aos.css";

class Root extends React.Component {
  state = {
    twitter: [],
    article: [],
    note: [],
    isModalOpen: false
  };


  addItem = (e, newItem) => {
    e.preventDefault();

    this.setState(prevState => ({
      [newItem.type]: [...prevState[newItem.type], newItem]
    }));

    this.closeModal();
  };

  openModal = () => {
    this.setState({
      isModalOpen: true
    });
  };

  closeModal = () => {
    this.setState({
      isModalOpen: false
    });
  };


  // useEffect(() => {
  //   Aos.init({duration:2000});
  // }, []) ;

  render() {

    Aos.init({duration:2000});
    const { isModalOpen } = this.state;
    const contextElements = {
      ...this.state,
      addItem: this.addItem
    };

    return (
      // <BrowserRouter>
      //   <AppContext.Provider value={contextElements}>
      //     <Header openModalFn={this.openModal} />
      //     <Switch>
      //       <Route exact path="/" component={TwittersView} />
      //       <Route path="/articles" component={ArticlesView} />
      //       <Route path="/notes" component={NotesView} />
      //     </Switch>
      //     { isModalOpen && <Modal closeModalFn={this.closeModal} /> }
      //   </AppContext.Provider>
      // </BrowserRouter>

      <div id="website">
        <div id="logo"><Logo /></div>
        <div id="nav">
          <div id="navItem">Portfolio |</div>
          <div id="navItem">&nbsp;Co wykonujemy |</div>
          <div id="navItem">&nbsp;Oferta |</div>
          <div id="navItem">&nbsp;Kontakt</div>
        </div>
        <div id="whatWeDo">
          <h4 >#Zostań w domu, remont zostaw nam.</h4>
          <h4>Co wykonujemy?</h4>
          <div id="SectionContent">
            Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean
            commodo ligula eget dolor. Aenean massa. Cum sociis natoque
            penatibus et magnis dis parturient montes, nascetur ridiculus mus.
            Donec quam felis, ultricies nec, pellentesque eu, pretium quis, sem.
            Nulla consequat massa quis enim. Donec pede justo, fringilla vel,
            aliquet nec, vulputate eget, arcu. In enim justo, rhoncus ut,
            imperdiet a, venenatis vitae, justo. Nullam dictum felis eu pede
            mollis pretium. Integer tincidunt. Cras dapibus. Vivamus elementum
            semper nisi. Aenean vulputate eleifend tellus. Aenean leo ligula,
            porttitor eu, consequat vitae, eleifend ac, enim. Aliquam lorem
            ante, dapibus in, viverra quis, feugiat a, tellus. Phasellus viverra
            nulla ut metus varius laoreet. Quisque rutrum. Aenean imperdiet.
            Etiam ultricies nisi vel augue. Curabitur ullamcorper ultricies
            nisi. Nam eget dui. Etiam rhoncus. Maecenas tempus, tellus eget
