      //       <Route path="/notes" component={NotesView} />
      //     </Switch>
      //     { isModalOpen && <Modal closeModalFn={this.closeModal} /> }
      //   </AppContext.Provider>
      // </BrowserRouter>

        <div id="website">

          <div id="logo">
              interBuild LOGO
          </div>
          <div id="nav">
            <div id="navItem">
              Portfolio
            </div>
            <div id="navItem">
              Co wykonujemy
            </div>
            <div id="navItem">
              Oferta
            </div>
            <div id="navItem">
