            </div>
          </div>
          <div id="portfolio">
            <div id="beforeAfterImgs">
              <div id="beforeImg">
                BEFORE
              </div>
              <div id="afterImg">
                AFTER
              </div>
            </div>
            <div id="beforeAfterImgs">
              <div id="beforeImg">
                BEFORE 2
              </div>
              <div id="afterImg">
                AFTER 2
              </div>
            </div>
            <div id="beforeAfterImgs">
              <div id="beforeImg">
                BEFORE 3
              </div>
              <div id="afterImg">
                AFTER 3
              </div>
            </div>
          </div>
          <div id="whatWeDo">

          </div>
          <div id="offer">

          </div>
          <div id="contact">

          </div>
          <footer id="footer">

          </footer>
        </div>   
      );
  }
}

export default Root;

